package Programes;

import org.openqa.selenium.By;
import org.openqa.selenium.WebDriver;
import org.openqa.selenium.chrome.ChromeDriver;

public class Gmail {
	
	public WebDriver driver;
	
	
	
	public void openurl() {
		
		System.setProperty("webdriver.chrome.driver", "E:\\MT - Programes\\chromedriver.exe\\");
		driver = new ChromeDriver();
		driver.get("http://gmail.com");
		
	}

	
	public void login(){
		
		
		
		driver.findElement(By.id("identifierId")).sendKeys("veldurthisharath@gmail.com");
		driver.findElement(By.xpath("/html/body/div[1]/div[1]/div[2]/div/div[2]/div/div/div[2]/div/div[2]/div/div[1]/div/div/button/div[2]")).click();
		driver.findElement(By.xpath("/html/body/div[1]/div[1]/div[2]/div/div[2]/div/div/div[2]/div/div[1]/div/form/span/section/div/div/div[1]/div[1]/div/div/div/div/div[1]/div/div[1]/input")).sendKeys("amma2161");
		driver.findElement(By.xpath("/html/body/div[1]/div[1]/div[2]/div/div[2]/div/div/div[2]/div/div[2]/div/div[1]/div/div/button/div[2]")).click();
			
		
	}
	
	
	public void composemail() {
		
		driver.findElement(By.xpath("/html/body/div[7]/div[3]/div/div[2]/div[1]/div[1]/div[1]/div/div/div/div[1]/div/div")).click();
		driver.findElement(By.id(":pr")).sendKeys("veldurthisharath@gmail.com");
		driver.findElement(By.id(":p9")).sendKeys("Resume");
		driver.findElement(By.id(":qe")).sendKeys("Body Message");
		driver.findElement(By.xpath("//*[@id=\":oz\"]")).click();
		
		
	}
	
	
	public void signout() {
		
		driver.findElement(By.xpath("/html/body/div[7]/div[3]/div/div[1]/div[3]/header/div[2]/div[3]/div[1]/div[2]/div/a/img")).click();
		driver.findElement(By.id("gb_71")).click();
		
	}
	
	
	
	public static void main(String[] args) {
		

		
		Gmail R = new Gmail();
		R.openurl();
		R.login();
		R.composemail();
		R.signout();
		
		
		
		
	}

}
